import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, OutletContext, Router } from '@angular/router';
import { Olympic } from 'app/core/models/olympic';
import { OlympicService } from 'app/core/services/olympic.service';
import { Observable, filter, map, tap } from 'rxjs';
import { NgxChartsModule} from '@swimlane/ngx-charts';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';


@Component({
  selector: 'app-country',
  templateUrl: './country.component.html',
  styleUrl: './country.component.scss'
})
export class CountryComponent implements OnInit{

  public countryInfo!: Olympic | undefined
  public chartData: {"name":string, "series":{}[]}[] = []
  public numberOfEntries !: number

  public totalMedalsCount: number = 0; // Propriété pour stocker le nombre total de médailles
  public totalAthleteCount: number = 0; // Propriété pour stocker le nombre total d'athlètes

  constructor(private route: ActivatedRoute,
              private olympicService: OlympicService,
              private router: Router) {}


  //chartConf
  legend: boolean = true;
  showLabels: boolean = true;
  animations: boolean = true;
  xAxis: boolean = true;
  yAxis: boolean = true;
  showYAxisLabel: boolean = true;
  showXAxisLabel: boolean = true;
  xAxisLabel: string = 'Year';
  yAxisLabel: string = 'Population';
  timeline: boolean = true;
  colorScheme = {
    domain: ['#5AA454', '#E44D25', '#CFC0BB', '#7aa3e5', '#a8385d', '#aae3f5']
  };

  ngOnInit(): void {
      
    //recuperation de l'info dans l'url
    const countryLabel = this.route.snapshot.params['country']//.charAt(0).toUpperCase() + this.route.snapshot.params['country'].slice(1)
    //console.log(countryLabel)
    
    
    

    //recuperation de l'objet contenant les infos du pays
    /*    this.olympicService.getDataByCountry(countryLabel).pipe(tap(value => value.forEach(participation => this.numberOfEntries = participation.participations.length)),
       map(value => this.chartData.push({name:countryLabel,series:value[0].participations.map(value => {
        return{
          name: value.year.toString(),
        value: value.medalsCount,
        }
       })
      })
       ))
       .subscribe()
  }
 */
  //const countryLabel = this.route.snapshot.params['country'];

    this.olympicService.getDataByCountry(countryLabel).subscribe(data => {
      this.countryInfo = data[0]; // Supposons qu'un seul objet est retourné pour le pays
      this.numberOfEntries = this.countryInfo.participations.length;

      // Calculer le nombre total de médailles et d'athlètes
      this.totalMedalsCount = this.countryInfo.participations.reduce((total, participation) => total + participation.medalsCount, 0);
      this.totalAthleteCount = this.countryInfo.participations.reduce((total, participation) => total + participation.athleteCount, 0);

      // Préparer les données pour le graphique
      this.chartData = [{
        name: countryLabel,
        series: this.countryInfo.participations.map(participation => ({
          name: participation.year.toString(),
          value: participation.medalsCount
        }))
      }];
    });
  }
}
