import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, OutletContext } from '@angular/router';
import { Olympic } from 'app/core/models/olympic';
import { OlympicService } from 'app/core/services/olympic.service';
import { valueOrDefault } from 'chart.js/dist/helpers/helpers.core';
import { Observable, filter, map, tap } from 'rxjs';
import { NgxChartsModule } from '@swimlane/ngx-charts';


@Component({
  selector: 'app-country',
  templateUrl: './country.component.html',
  styleUrl: './country.component.scss'
})
export class CountryComponent implements OnInit{

  public countryInfo!: Olympic | undefined
  public chartData!: {}

  constructor(private route: ActivatedRoute,
              private olympicService: OlympicService) {}


  ngOnInit(): void {
      
    //recuperation de l'info dans l'url
    const countryLabel = this.route.snapshot.params['country']

    //recuperation de l'objet contenant les infos du pays

/*     this.olympicService.getOlympics().pipe(tap
      (data => {this.countryInfo = data.find (value => value.country = countryLabel)})).subscribe()
 */
       this.olympicService.getDataByCountry(countryLabel).pipe(map(value => this.chartData = value[0].participations.map(participation => {return {
        name:participation.year,
        value:participation.medalsCount}
      }))).subscribe()
  }

}
<p>country works! {{countryInfo?.participations}}</p>

<p>{{ chartData }}</p>

<ngx-charts-line-chart
  [view]="view"
  [scheme]="colorScheme"
  [legend]="legend"
  [showXAxisLabel]="showXAxisLabel"
  [showYAxisLabel]="showYAxisLabel"
  [xAxis]="xAxis"
  [yAxis]="yAxis"
  [xAxisLabel]="xAxisLabel"
  [yAxisLabel]="yAxisLabel"
  [timeline]="timeline"
  [results]="chartData"
  (select)="onSelect($event)"
  (activate)="onActivate($event)"
  (deactivate)="onDeactivate($event)"
  >
</ngx-charts-line-chart>
