import { Component, OnInit } from '@angular/core';
import { Observable, of,tap,map } from 'rxjs';
import { OlympicService } from 'app/core/services/olympic.service';
import { Olympic } from 'app/core/models/olympic';
import { MedalCount } from 'app/core/models/medal-count';
import { Router } from '@angular/router';
import { NgxChartsModule} from '@swimlane/ngx-charts';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  


  public olympics$: Observable<Olympic[]> = of([]);
  public medal!:Observable<MedalCount[]>;
  public yearsOfJo : Set<number> = new Set<number>()
 
  public numberCountry!:number
  constructor(private olympicService: OlympicService, private router:Router) {};

  //fonction pour renvoyer l'utilisateur vers la page contenant les infos du pays
  onSelect(event: any): void {
    if (event.label) { 
      //console.log('Chart clicked:', event)  ;
      this.router.navigateByUrl(`country/${event.label}`)
    }else{
      this.router.navigateByUrl(`country/${event}`)
    }
  }
  
  

  ngOnInit(): void {

    //nombre de pays
    this.olympics$ = this.olympicService.getOlympics();

    //nombre de jo
    this.olympics$.pipe(
      map(value => value.map(
        country => country.participations.forEach(participation => {
          this.yearsOfJo.add(participation.year)
        }))
        )
        ).subscribe()
  
   

   //passer par le async
   this.medal = this.olympicService.getMedalCount()
   console.log(this.medal)
   
  
   
   

  }
}
 
 <div class="container" *ngIf="medal">
    <!-- Première ligne -->
    <div class="row justify-content-center">
        <div class="col-12 d-flex justify-content-center" >
         <div ><h4 class="border text-white rounded p-2" style="background-color: teal;">Medals per Country</h4>
        </div> 
      </div>
    </div>
    <!-- Deuxième ligne -->
    <div class="d-flex justify-content-sm-center">
      <div class="p-2 bd-highlight  text-center border border-info rounded m-3" >Number of entries <br /> <b>{{(olympics$ | async)?.length}}</b> </div>
        <div class="p-2 bd-highlight text-center border border-info rounded m-3" >total number medal <br />  <b>{{yearsOfJo.size}}</b></div>
      </div>

  <ngx-charts-pie-chart class="row justify-content-center"
    [results]="medal | async"
    [legend]="false"
    [labels]="true"
    [view]=[500,300]
    (select)="onSelect($event)"
  >
</ngx-charts-pie-chart>
</div>
