import { Component, OnInit } from '@angular/core';
import { Observable, of,tap,map } from 'rxjs';
import { OlympicService } from 'app/core/services/olympic.service';
import { Olympic } from 'app/core/models/olympic';
import { MedalCount } from 'app/core/models/medal-count';
import { Router } from '@angular/router';
import { NgxChartsModule} from '@swimlane/ngx-charts';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent implements OnInit {
  


  public olympics$: Observable<Olympic[]> = of([]);
  public medal!:Observable<MedalCount[]>;
  public yearsOfJo : Set<number> = new Set<number>()
 
  public numberCountry!:number
  constructor(private olympicService: OlympicService, private router:Router) {};

  //fonction pour renvoyer l'utilisateur vers la page contenant les infos du pays
  onSelect(event: any): void {
    if (event.label) { 
      //console.log('Chart clicked:', event)  ;
      this.router.navigateByUrl(`country/${event.label}`)
    }else{
      this.router.navigateByUrl(`country/${event}`)
    }
  }
  
  

  ngOnInit(): void {

    //nombre de pays
    this.olympics$ = this.olympicService.getOlympics();

    //nombre de jo
    this.olympics$.pipe(
      map(value => value.map(
        country => country.participations.forEach(participation => {
          this.yearsOfJo.add(participation.year)
        }))
        )
        ).subscribe()
  
   

   //passer par le async
   this.medal = this.olympicService.getMedalCount()
   
  
   
   

  }
}

    <div class="container">
      <!-- Première ligne -->
      <div class="w-50 p-3 row aligne-item-center" id="myCenteredDiv">
          <div class="col-sm text-center rounded" style="padding: 10px;border: 2px solid teal; margin: 10px; background-color: teal;">Medal By Country</div>
      </div>
      <!-- Deuxième ligne -->
      <div class="w-100 p-3 row justify-content-center">
          <div class="col-sm text-center rounded" style="border: 1px solid teal; margin: 10px;">Nombre de pays <br /> <b>{{ (olympics$ | async)?.length }}</b></div>
          <div class="col-sm text-center rounded" style="border: 1px solid teal; margin: 10px;">Nombre de JO <br /> <b>{{yearsOfJo.size}}</b></div>
      </div>
 

      <div class="container">
        <div class="row justify-content-center">
          <div class="col-12 d-flex justify-content-center">
            <!-- Élément au milieu -->
            <div style="background-color: #f0f0f0; padding: 20px;">Médaille d'or</div>
          </div>
        </div>
        <div class="row justify-content-between">
          <!-- Deux éléments sur une ligne en dessous -->
          <div class="col-5" style="background-color: #dcdcdc; padding: 20px;">Médaille d'argent</div>
          <div class="col-5" style="background-color: #cd7f32; padding: 20px;">Médaille de bronze</div>
        </div>
      </div>


    <ngx-charts-pie-chart 
      [results]="medal | async"
      [legend]="true"
      [labels]="true"
      (select)="onSelect($event)"
    ></ngx-charts-pie-chart>

  
  
</div>
